import React from "react";
// import reactDom from "react-dom";

const Counter = () => {
    let count = 0;
    const addOn = ()=>console.log('add one');
    const minus1 = ()=>console.log('substract one')
    const reset = ()=> console.log('count = 0')
    return (
        <div>
            <h1>Counter : {count}</h1>
            <button id='my-id' className='myBtn' onClick= {addOn}>+1</button>
            <button onClick ={minus1}>-1</button>
            <button onClick ={reset}>Reset</button>
        </div>
    )
}


export default Counter;
