import React from "react";
// import reactDom from "react-dom";

const Counter = () => {
    let count = 0;
    const addOn = (cnt)=>cnt++;
    return (
        <div>
            <h1>Counter : {count}</h1>
            <button id='my-id' className='myBtn' onClick= {addOn(count)}>+1</button>
        </div>
    )
}


export default Counter;
