import React from 'react';
import ReactDOM from 'react-dom';
// // import App from './App';
// import Counter from './Counter';

// ReactDOM.render(
//   <React.StrictMode>

//     {/*<App />*/}
//     <Counter />
//   </React.StrictMode>,
//   document.getElementById('root')
// );
const app = {
  title : 'Indecision App',
  subtitle : 'How to Be better in this life',
  options : []
};

const onFormSubmit = (e) => {
    e.preventDefault();
    const option = e.target.elements.option.value;
    if(option){
      app.options.push(option);
      e.target.elements.option.value = ''
    }
    renderIndecisionApp();
}

const removeAll = () => {
  app.options = []
  renderIndecisionApp();
}

const whatDo = () => console.log(app.options[0])

const appRoot = document.getElementById('root');
const renderIndecisionApp = () =>{ 
    const template = (
            <div>
              <h1>{app.title}</h1>
              {app.subtitle && <p>{app.subtitle}</p>}
              {app.options.length > 0 ? <p>Here are your options</p> : "No options"}
              {/*<p>Number of missions is : {app.options.length}</p>}*/}
              <button onClick={whatDo}>What I should do ?</button>
              <button onClick={removeAll}>Remove All</button>
              <ol>
                {app.options.map((option)=><li key={option}>{option}</li>)}
              </ol>
              <form onSubmit={onFormSubmit}>
                <input type='text' name='option' />
                <button >Add Option</button>
              </form>
            </div>
    );

    ReactDOM.render(template,appRoot);
}

renderIndecisionApp();
